    protected async Task Delete()
    {
        SchedulingSystemForELSOContext db = new SchedulingSystemForELSOContext();
        var log_user = db.LoginTable.Where(l => l.Username == new_user.Username).FirstOrDefault();
        var meetings = db.TimeSlotsTable.Where(t => t.TeacherUsername == new_user.Username).ToList();
        for(int i=0; i<meetings.Count;i++)
        {
            var edit_meeting = db.RescheduleDeleteTable.Where(r => r.MeetingId == meetings[i].Id).FirstOrDefault();
            db.RescheduleDeleteTable.Remove(edit_meeting);
            var students = db.BookMeetingTable.Where(b => b.MeetingId == meetings[i].Id).ToList();
            for(int j=0;j<students.Count;j++)
            {
                db.BookMeetingTable.Remove(students[j]);
            }
            db.TimeSlotsTable.Remove(meetings[i]);
        }
        db.LoginTable.Remove(log_user);
        db.TeacherTable.Remove(new_user);
        db.SaveChanges();
        toastService.ShowSuccess("Teacher has been deleted successfully.");
        navigationManager.NavigateTo("/Setting_Page/" + Admin_Username);
    }
    protected async Task Cancel()
    {
        navigationManager.NavigateTo("/Setting_Page/" + Admin_Username);
